mapper.update:
UPDATE review
SET review_content = #{reviewContent},
review_image = #{reviewImage},
review_point = #{reviewPoint}
WHERE review_no = #{reviewNo}

UPDATE review
SET review_like = review_like + 1
WHERE review_no = #{reviewNo}

UPDATE review
SET review_like = review_like - 1
WHERE review_no = #{reviewNo}
AND review_like > 0;
